    $('#youtube-edit-id').val($(node).data("youtube-id"));
    $('#vimeo-edit-id').val($(node).data("vimeo-id"));
    $('#video_edit_priority').val($(node).data("priority"));
    $('#video_edit_status').val($(node).data("status"));
    $('#video_edit_title').val($(node).data("title"));
